create table ldb_persons (person_id integer not null auto_increment unique, person_name varchar(100) not null character set utf8, graduation varchar(20) character set utf8);
create table ldb_groups (group_id integer not null auto_increment unique, year varchar(20) character set utf8 not null, group_name varchar(100) character set utf8 not null, index year_ind(year));
create table ldb_attendances (person_id integer, group_id integer, role varchar(80) character set utf8, primary key (person_id, year), index person_ind (person_id), foreign key (person_id) references ldb_persons(person_id), foreign key (group_id) references ldb_groups(group_id));
create table ldb_photos (person_id integer not null, year varchar(20) character set utf8 not null, photo varchar(200) character set utf8, photo_small varchar(200) character set utf8, primary key (person_id, year), foreign key (person_id) references ldb_persons(person_id), foreign key (year) references ldb_groups(year));
